<?php if($model->getImagePath('photo')):?><?php echo CHtml::image("http://medbooking.com/".$model->getImagePath('photo'),$model->fio,array('title'=>$model->fio));?><?php else:?><?php if(!empty($model->gender)&&$model->gender==2):?><?php echo CHtml::image("http://medbooking.com/img/no_img_woman.png",$model->fio,array('title'=>$model->fio));?><?php else:?><?php echo CHtml::image("http://medbooking.com/img/no_img_man.png",$model->fio,array('title'=>$model->fio));?><?php endif;?><?php endif;?>