<?php if(!empty($this->category)):?><?php $price=$model->firstPriceCategory($this->category); ?><?php if(!empty($price)):?><?php foreach($price as $value):?><?php if($value->price!='00'):?><span class="price">Прием: <?php echo $value->price?$value->price.' руб.':'&mdash;';?></span><?php else:?><span class="price">Бесплатно</span><?php endif;?><?php break; ?><?php endforeach;?><?php endif;?><?php else:?><?php if(!empty($model->firstPrice)):?><?php foreach($model->firstPrice as $value):?><?php if($value->price!='00'):?><span class="price">Прием: <?php echo $value->price?$value->price.' руб.':'&mdash;';?></span><?php else:?><span class="price">Бесплатно</span><?php endif;?><?php break; ?><?php endforeach;?><?php endif;?><?php endif;?>